package com.xinyan.mms.materials.query;/** * 物料表 */public class QueryTab_Materials {    private int materials_id;    private String materials_coding;    private String materials_name;    private int unit_coding;    private String materials_specification;    private String materials_useprocess;    private int type_coding;    private int materials_status;    private String materials_remark;    @Override    public String toString( ) {        return "QueryTab_Materials{" +                "materials_id=" + materials_id +                ", materials_coding='" + materials_coding + '\'' +                ", materials_name='" + materials_name + '\'' +                ", unit_coding=" + unit_coding +                ", materials_specification='" + materials_specification + '\'' +                ", materials_useprocess='" + materials_useprocess + '\'' +                ", type_coding=" + type_coding +                ", materials_status=" + materials_status +                ", materials_remark='" + materials_remark + '\'' +                '}';    }    public String getMaterials_useprocess( ) {        return materials_useprocess;    }    public void setMaterials_useprocess( String materials_useprocess ) {        this.materials_useprocess = materials_useprocess;    }    public String getMaterials_remark( ) {        return materials_remark;    }    public void setMaterials_remark( String materials_remark ) {        this.materials_remark = materials_remark;    }    public int getMaterials_id( ) {        return materials_id;    }    public void setMaterials_id( int materials_id ) {        this.materials_id = materials_id;    }    public String getMaterials_coding( ) {        return materials_coding;    }    public void setMaterials_coding( String materials_coding ) {        this.materials_coding = materials_coding;    }    public String getMaterials_name( ) {        return materials_name;    }    public void setMaterials_name( String materials_name ) {        this.materials_name = materials_name;    }    public int getUnit_coding( ) {        return unit_coding;    }    public void setUnit_coding( int unit_coding ) {        this.unit_coding = unit_coding;    }    public String getMaterials_specification( ) {        return materials_specification;    }    public void setMaterials_specification( String materials_specification ) {        this.materials_specification = materials_specification;    }    public int getType_coding( ) {        return type_coding;    }    public void setType_coding( int type_coding ) {        this.type_coding = type_coding;    }    public int getMaterials_status( ) {        return materials_status;    }    public void setMaterials_status( int materials_status ) {        this.materials_status = materials_status;    }}